declare
  @v_load_id int
  begin
   exec semarchy_repository.dbo.get_new_loadid
    @return_value_argument=@v_load_id OUTPUT,
    @p_data_location_name = 'CustomerB2c', /* Data Location Name (as in the UI) */
    @p_program_name = 'ADF',		/*  Informational. Identifies the Middleware */
    @p_load_description = N'Blob XSLS to XDM',/* Informational. Describes the load. */
    						/* Note the N to allow accented characters */
    @p_username = 'semadmin'		/* User initializing the load. */
    select @v_load_id as loadid
end
